CREATE TABLE IF NOT EXISTS "social_users" ("id" bigserial, "email" varchar(255), "name" varchar(255), "logs" integer DEFAULT 1, "user_token" varchar(255), "inserted_at" timestamp(0) NOT NULL, "updated_at" timestamp(0) NOT NULL, PRIMARY KEY ("id")) ;CREATE UNIQUE INDEX "social_users_email_index" ON "social_users" ("email") ;CREATE TABLE IF NOT EXISTS "counter" ("id" bigserial, "count" integer, "inserted_at" timestamp(0) NOT NULL, "updated_at" timestamp(0) NOT NULL, PRIMARY KEY ("id")) ;CREATE TABLE IF NOT EXISTS "users" ("id" bigserial, "email" varchar(255) NOT NULL, "hashed_password" varchar(255) NOT NULL, "confirmed_at" timestamp(0), "inserted_at" timestamp(0) NOT NULL, "updated_at" timestamp(0) NOT NULL, PRIMARY KEY ("id")) ;CREATE UNIQUE INDEX "users_email_index" ON "users" ("email") ;CREATE TABLE IF NOT EXISTS "users_tokens" ("id" bigserial, "user_id" bigint NOT NULL, CONSTRAINT "users_tokens_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "users"("id") ON DELETE CASCADE, "token" bytea NOT NULL, "context" varchar(255) NOT NULL, "sent_to" varchar(255), "inserted_at" timestamp(0) NOT NULL, PRIMARY KEY ("id")) ;CREATE INDEX "users_tokens_user_id_index" ON "users_tokens" ("user_id") ;CREATE UNIQUE INDEX "users_tokens_context_token_index" ON "users_tokens" ("context", "token") ;